css:
p {
            text-align: center;
            margin: 0; /* Remove default margin for <p> */
        }

        p.left-align {
            text-align: left;
        }

        p.right-align {
            text-align: right;
        }

        /* Add some spacing between elements */
        p.spacing {
            margin-bottom: 10px;
        }

        /* Additional styles for roles */
        .roles {
            display: flex;
            justify-content: space-between;
        }

        .roles p {
            margin: 0; /* Remove margin for roles */
        }
